// src/App.js
import React, { useEffect } from 'react';
import './App.css';

function App() {
  // Load the external Dialogflow script after component mounts
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://www.gstatic.com/dialogflow-console/fast/df-messenger/prod/v1/df-messenger.js';
    script.async = true;
    document.body.appendChild(script);
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to React with Dialogflow</h1>
        <p>This is your main content area with an embedded chatbot.</p>
      </header>

      {/* Embed Dialogflow Messenger Widget */}
      <div
        dangerouslySetInnerHTML={{
          __html: `
            <df-messenger
              oauth-client-id="INSERT_OAUTH_CLIENT_ID"
              location="us-central1"
              project-id="nerdery-technology-gen-ai"
              agent-id="6f68b946-9042-4a05-b26c-98db9074a37d"
              language-code="en"
              max-query-length="-1">
              <df-messenger-chat-bubble chat-title="Purina Chatbot PoC"></df-messenger-chat-bubble>
            </df-messenger>
          `,
        }}
      />

      {/* Add custom CSS styles */}
      <style>
        {`
          df-messenger {
            z-index: 999;
            position: fixed;
            bottom: 16px;
            right: 16px;
          }
        `}
      </style>
    </div>
  );
}

export default App;
